public courseListRead(): Promise<ICourse[]> {
    return this.courseModel.find().sort({
      index: 1
    }).populate('instructor').populate({
      path: 'categories',
      populate: {
        path: 'posts',
        model: 'CourseCategoryPost'
      }
    }).exec();
  }

